    // Signup Engine
    if (isset($_POST["signup"])) {
        $_SESSION["email_verify"] = false;
        $password_hash = password_hash($_POST["password"], PASSWORD_DEFAULT);
        $password = $_POST["password"];
        $email = $_POST["email"];
        $name = $_POST["name"];
        
        // Function to validate password strength
        function validatePassword($password) {
            // Validate password strength
            $uppercase = preg_match('@[A-Z]@', $password);
            $lowercase = preg_match('@[a-z]@', $password);
            $number    = preg_match('@[0-9]@', $password);
            $specialChars = preg_match('@[^\w]@', $password);

            if(!$uppercase || !$lowercase || !$number || !$specialChars || strlen($password) < 8) {
                return false;
            }else{
                return true;
            }
        }
    
        // Function to validate email address
        function validateEmail($email) {
            return filter_var($email, FILTER_VALIDATE_EMAIL);
        }
    
        // Function to validate name (letters only)
        function validateName($name) {
            return preg_match('/^[a-zA-Z ]+$/', $name);
        }
    
        // Retrieve form data
        if (isset($_POST['password'], $_POST['email'], $_POST['name'])) {
            $password = $_POST['password'];
            $email = $_POST['email'];
            $name = $_POST['name'];
        
            // Initialize error code
            $errorCode = 0;
        
            // Validate password
            if (!validatePassword($password)) {
                $errorCode = 2; // Password error
            }
        
            // Validate email
            if (!validateEmail($email)) {
                $errorCode = 3; // Email error
            }
        
            // Validate name (letters only)
            if (!validateName($name)) {
                $errorCode = 4; // Name error
            }
        
            // Handle errors
            if ($errorCode > 0) {
                $_SESSION['pre_email'] = $email; 
                $_SESSION['pre_name'] = $name;
                header("Location: ?error=$errorCode&active=up");
                exit;
            }
        } else {
            $url = "?error=5";
            header("Location: $url");
            exit;
        }
        
        while (true) {
            $user_id = rand(10000000,99999999);
