            $result = $conn->query($sql);
            
            if (!mysqli_num_rows($result) > 0) {
                break;
            }
        }

        $sql = "SELECT * FROM `userlogin` WHERE email = '$email'";
        $result = $conn->query($sql);
        if (!mysqli_num_rows($result) > 0) {
            $_SESSION['user_id'] = $user_id;
            $_SESSION['password_hash'] = $password_hash;
            $_SESSION['email'] = $email; 
            $_SESSION['name'] = $name;

            $url = "../verify/?back=$back";
            header("Location: $url");
            exit;
        } else {
            $url = "?error=1";
            header("Location: $url");
            exit;
        }
    }
